import {
  addDoc,
  collection,
  doc,
  onSnapshot,
  orderBy,
  query,
  Timestamp,
  where,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { db } from "./firebase";

function Chat() {
  const [msg, setMsg] = useState("");
  const [msgs, setMsgs] = useState();
  const chatstyle = {
    width: "40%",
    height: "600px",
    backgroundColor: "grey",
    borderRadius: "20px",
  };
  const inputstyles = {
    width: "39%",
    height: window.innerHeight - 690,
    backgroundColor: "smoke-white",
    borderRadius: "8px",
  };
  const addMessage = async (e) => {
    e.preventDefault();
    setMsg(e.target.value);

    console.log(e.target.value);
    if (e.code === "Enter") {
      await addDoc(collection(db, "messages"), {
        msg: msg,
        sentTime: new Date(),
      });
      console.log("message stored in firebase");
    }
  };

  let textmsg = [];
  useEffect(() => {

    const unsub = onSnapshot(
      collection(db, "messages"),
      orderBy("sentTime"),
      (docs) => {
        docs.forEach((doc) => textmsg.push(doc.data()));
        // docs.forEach((doc) => console.log(doc.data()));
      }
    );
    console.log(msgs);
    setMsgs(textmsg);
  }, []);

  return (
    <>
      <div style={chatstyle}>
      </div>
      <input type="text" style={inputstyles} onKeyUp={(e) => addMessage(e)} />
    </>
  );
}

export default Chat;
