import { addDoc } from "firebase/firestore";
import React, { useState } from "react";
import { db } from "./firebase";

function Chat() {
  const [msg, setMsg] = useState("");
  const chatstyle = {
    width: "40%",
    height: "600px",
    backgroundColor: "grey",
    borderRadius: "20px",
  };
  const inputstyles = {
    width: "39%",
    height: window.innerHeight - 690,
    backgroundColor: "smoke-white",
    borderRadius: "8px",
  };
  const addMessage = async (e) => {
    console.log(e);
    if (e.code === "Enter") {
      setMsg(e.target.value);
      await addDoc(db, "messages", { msg: msg, sentTime: new Date() });
    }
  };
  return (
    <>
      <div style={chatstyle}></div>
      <input type="text" style={inputstyles} onKeyDown={(e) => addMessage(e)} />
    </>
  );
}

export default Chat;
