import {
  addDoc,
  collection,
  doc,
  onSnapshot,
  orderBy,
  query,
  Timestamp,
  updateDoc,
  where,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth, db } from "./firebase";
import Message from "./Message";


function Chat() {
const [user] = useAuthState(auth)
console.log(user)

  const [msg, setMsg] = useState("");
  const [msgs, setMsgs] = useState();
  const chatstyle = {
    width: "40%",
    height: "600px",
    backgroundColor: "grey",
    borderRadius: "20px",
    overflow:"scroll"
  };
  const inputstyles = {
    width: "39%",
    height: window.innerHeight - 690,
    backgroundColor: "smoke-white",
    borderRadius: "8px",
    overFlow:"scroll"
  };
  const addMessage = async (e) => {
    e.preventDefault();
    setMsg(e.target.value);

    console.log(e.target.value);
    if (e.code === "Enter") {
    const docref=  await addDoc(collection(db, "messages"), {
        msg: msg,
        user:user.displayName,
        uid:user.uid,
        sentTime: new Date(),
      });
      console.log(docref.id)
      // await updateDoc(docref, {
      //   docid:docref.id
      // });
      console.log("message stored in firebase");
    }
  };

  let textmsg = [];
  useEffect(() => {
    const q = query(collection(db, "messages"), orderBy("sentTime"));
    const unsub = onSnapshot(q, (querySnapshot) => {
      let messages = [];
      querySnapshot.forEach((doc) =>
        messages.push({ ...doc.data(), id: doc.id })
      );
    setMsgs(messages)

    });
    return ()=>unsub()
  }, []);

  return (
    <>
      <div style={chatstyle}>
        {msgs&&msgs.map((m) => (
          <Message msg={m}/>
        ))}
      </div>
      <input type="text" style={inputstyles} onKeyUp={(e) => addMessage(e)} />
    </>
  );
}

export default Chat;
